<?php/*** $action variabile che contiene il nome dell'area corrente**/	add_to_debug("Azione",  $action);	switch ($action)	{		case "insert":			$rows = array();						for($i=0; $i<count($_REQUEST["prot"]); $i++)			{				$ar=0;				if (isset($_REQUEST["ar"][$i]))				{					$ar=1;					}				$rows[$i]=array("prot"=>$_REQUEST["prot"][$i],				"destinatario"=>$_REQUEST["destinatario"][$i], "citta"=>$_REQUEST["citta"][$i],				"provincia"=>$_REQUEST["provincia"][$i], "cap"=>$_REQUEST["cap"][$i],				"raccomandata"=>$_REQUEST["numracc"][$i], "ar"=>$ar,				"peso"=>$_REQUEST["peso"][$i]);			}						$time = str_replace('/', '-', $_SESSION["date"]);			$time=strtotime($time);			if (verify_nonce($_REQUEST["nonce"]))			{				$id=get_next_distinta_id(date("Y"), $time);				$result = insert_new_raccomandata($id, $time, $rows);			}			return new ReturnedArea("clpservice","default","distinte");		case "back":				verify_nonce($_REQUEST["nonce"]);				return new ReturnedArea("clpservice","default","distinte");		default:			$year=date("Y");				if (isset($_SESSION["latest_year_viewed"]))			{			    $year = $_SESSION["latest_year_viewed"];			}			return new ReturnedPage("nuova_raccomandata.php", array("date"=>$_SESSION["date"],"latest_year_viewed"=>$year));	}?>?>